import axios from 'axios'
import React, { useEffect, useState } from 'react'
import Card from '../Components/Card'

export default function Films() {
    const [director, setDirectorName] = useState("")
    const [filmsArray, setFilmArray] = useState("")
    const apiFunction = async (e) => {
       e.preventDefault()
        try {
            return await new Promise((reject, resolve) => {
                const payload = {
                    url: "https://app.codescreen.com/api/assessments/films",
                    params: { directorName: director },
                    headers: {
                        'Authorization': 'Bearer ' + "8c5996d5-fb89-46c9-8821-7063cfbc18b1"
                    },
                    method: "GET"
                }
                axios(payload).then((result) => {
                    setFilmArray(result.data)
                })
            })
        } catch (error) {
            return console.log("error", error)
        }



    }
    // useEffect(() => {
    //     const timer = setTimeout(() => {
    //         if (director) {
    //             apiFunction()
    //         }
    //     }, 300)
    //     return () => clearTimeout(timer)
    // }, [director])
    //
    return (
        <div style={{ backgroundColor: "rgba(240, 230, 242, 0.8)", padding: "15px" }}>
            <div >
                <p style={{ text: "rgba(128, 109, 132, 0.95)", fontSize: "18px", fontWeight: 800 }}>Films Analysis Service </p>
                <form id = "input-form" >
                    <input type="text" id = "input-box"  value={director} onChange={(e) => setDirectorName(e.target.value)}></input>
                    <input type="submit" value="Submit" onClick = {(e) =>apiFunction(e)}></input>
                </form>

            </div>
            <Card />



        </div>
    )
}
