import axios from 'axios'
import React, { useState } from 'react'

export default function Films() {
    const [director , setDirectorName] = useState("")
    const apiFunction = async (e) =>{
        try {
            return await new Promise((reject, resolve) => {
                const payload = {
                    url: "https://app.codescreen.com/api/assessments/films",
                    params: { directorName: e.target.value ||  "Christopher Nolan" },
                    headers: {
                        'Authorization': 'Bearer ' + "8c5996d5-fb89-46c9-8821-7063cfbc18b1"
                    },
                    method: "GET"
                }
                axios(payload).then((result) => {
                    setDirectorName(result.data)
                })
            })
        } catch (error) {
            return console.log("error", error)
        }

    }
  return (
    <div>
        <div className="films-analysis-service">
        <p >Films Analysis Service </p>
         <input value = {director} onChange = {(e) => apiFunction(e)}></input>
        </div>

      <div onClick={() => apiFunction()}>List of Filmssdsdsdsdsdsd</div>

    </div>
  )
}
